import React, { Component } from 'react';
import './styles.css';

const NAME_LS = 'name';

export default class Mainscreen extends Component {
    constructor() {
        super();

        this.state = {
            name: ''
        }
        this.handleChangeName = this.handleChangeName.bind(this);
    }
    componentDidMount() {
        const name = localStorage.getItem(NAME_LS);

        if (name) {
          this.setState({name});
        }
        }

    handleChangeName(e) {
        this.setState({name: e.target.value});
        localStorage.setItem(NAME_LS, this.state.name)
    }
    handleSubmit = async e => {
        // e.preventDefault();
        this.setState({name: this.state.name})
    }

    render() {
        return (
            <div className="loginpage1">
                <form onSubmit={this.handleSubmit} noValidate>
                <div className="panel-heading">
                            <h1 className="panel-title">Hello, what's your name?</h1>
                          </div>
                    
                            <input type='text' name='name' 
                            autoComplete='off'
                            onChange={this.handleChangeName}
                            placeholder=""
                            value={this.state.name}  
                            noValidate />   
                            <div className="btn-group btn-group-lg" role="group" aria-label="...">
                                <label htmlFor='step2' id="continue-step2" className="continue">
                                    <div className="btn btn-default btn-primary btn-lg" onClick={this.handleSubmit}>Continue</div>
                                </label>
                            </div>    
                            </form> 
            </div>
        )
    }
}
