import React, { Component } from 'react';

function updateState(text){
    this.setState({text})
}

//passing state from parent to child
class Child1 extends Component {
    constructor(props) {
        super(props)
        this.state = {
            text: "Initial State"
        }
        updateState = updateState.bind(this)
    }
    render() {
        return (
            <div>
                <div >I am Child</div>
                <div >{this.state.text}</div>
            </div>
        )
    }
}

export default Child1;
